import numpy as npimport matplotlib.pyplot as pltclass Sudoku():    def __init__(self, filename):        self.sudoku_problem = np.zeros((9,9))        self.read(filename)        self.sudoku = self.sudoku_problem    def read(self, filename):        self.sudoku_problem = np.genfromtxt(filename,                                            delimiter=',').astype(int)    def show(self):         fig, ax = plt.subplots()        fig.patch.set_visible(False)        ax.axis('off')        ax.axis('tight')        ax.table(self.sudoku)        plt.show()        if __name__=='__main__':    sudoku = Sudoku('../data/sudoku_1.txt')    sudoku.show()